mapper.select:
SELECT count(post_id) FROM post_tbl

		AND post_title LIKE '%${postTitle}%'
		AND category_id = #{categoryId}

choose.when:
	SELECT * FROM
		(SELECT @rownum:=@rownum+1 AS rnum,sorted_tbl.*
		FROM (SELECT * FROM post_tbl

				post_id = #{postId}
				AND post_title LIKE '%${postTitle}%'
				AND category_id = #{categoryId}

			) sorted_tbl,(SELECT @rownum:=0) tmp ORDER BY post_date desc) result_tbl NATURAL JOIN subcategory_tbl
 	WHERE rnum >= #{start} AND rnum <= #{end}

choose.otherwise:
SELECT * FROM post_tbl NATURAL JOIN subcategory_tbl

	post_id = #{postId}
	AND post_title LIKE '%${postTitle}%'
	AND category_id = #{categoryId}

ORDER BY post_date desc

mapper.select:
SELECT * FROM (SELECT @rownum1:=@rownum1+1 AS rnum,sorted_tbl.* FROM (SELECT * FROM post_tbl NATURAL JOIN subcategory_tbl
category_id = #{categoryId}
ORDER BY post_date desc) sorted_tbl, (SELECT @rownum1:=0) tmp) result_tbl WHERE rnum = (SELECT rnum FROM (SELECT @rownum2:=@rownum2+1 AS rnum,sorted_tbl.* FROM (SELECT * FROM post_tbl
category_id = #{categoryId}
ORDER BY post_date desc) sorted_tbl, (SELECT @rownum2:=0) tmp) result_tbl WHERE post_id = #{postId})+1 OR rnum = (SELECT rnum FROM (SELECT @rownum3:=@rownum3+1 AS rnum,sorted_tbl.* FROM (SELECT * FROM post_tbl
category_id = #{categoryId}
ORDER BY post_date desc) sorted_tbl, (SELECT @rownum3:=0) tmp) result_tbl WHERE post_id = #{postId})-1

mapper.update:
UPDATE post_tbl

	post_title = #{postTitle},
	post_content = #{postContent},
	category_id = #{categoryId},
	thumbnail = #{thumbnail}


	post_id = #{postId}
